Fn::Join:
class toolbox {
        file { '/usr/local/sbin/puppetsimple.sh':
                owner => root, group => root, mode => 0755,
                content => "#!/bin/sh\npuppet agent --onetime --no-daemonize --verbose $1",
        }
}
service { 'httpd':
    ensure  => 'running',
    enable  => true,
    hasrestart => true,
}
class sites {
        file {['/etc','/etc/httpd']:
          ensure => directory
        }
        file { '/etc/httpd/conf.d':
                ensure       => directory,
                group        => root,
                mode         => 0644,
                notify       => Service['httpd'],
                owner        => root,
                path         => '/etc/httpd/conf.d',
                recurse      => true,
                source       => 'puppet:///files/sites'
        }
}
node 'web1.internal.adire.co.uk' {
        include sites
        include toolbox
}
node 'web2.internal.adire.co.uk' {
        include sites
        include toolbox
}
node 'web3.internal.adire.co.uk' {
        include sites
        include toolbox
}
